import React, { Component } from 'react';
import axios from 'axios';

export class Registar extends Component {
    static displayName = Registar.name;

    constructor(props) {
        super(props);
        this.state = {
            id: 0,
            name: '',
            dataNascimento: null,
            email: '',
            morada: '',
            codigo_postal: '',
            password: '',
            nif: '',
            contactos: '',
            localidade: '',
            type: ''
        };
    }

    mySubmitHandler = (event) => {
        event.preventDefault();
        alert("Falta definir as acoes para os eventos");
    }

    myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({ [nam]: val });
    }

    // !!!!!!!!!!!!!!!!!!!!!!!!!!!!!

    submitNew = (event) => {
        event.preventDefault();

        params = {
            'type': this.state.type
        }

        axios.post(`https://localhost:5001/contas`, params, {
            body: JSON.stringify({
                nome: this.state.name,
                email: this.state.email,
                password: this.state.password,
                dataNascimento: this.state.dataNascimento,
                morada: this.state.morada,
                nif: this.state.nif,
                codigo_postal: this.state.codigo_postal,
                contactos: this.state.contactos,
                Localidade: this.state.localidade
            })
        })
            .then(res => res.json())
            .then(conta => {
                this.props.addUserToState(conta);
                this.props.toggle();
                alert("Novo user " + conta);
            })
            .catch(err => console.log(err));
    }

    submitEdit = e => {
        e.preventDefault();
        fetch(`https://localhost:5001/contas/${this.state.id}`, {
            method: 'put',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                nome: this.state.name,
                password: this.state.password,
                email: this.state.email,
                dataNascimento: this.state.dataNascimento
            })
        })
            .then(() => {
                this.props.toggle();
                this.props.updateUserIntoState(this.state.id);
            })
            .catch(err => console.log(err));
    }

    // !!!!!!!!!!!!!!!!!!!!!!!!!!!!!!

    render() {
        return (
            <form onSubmit={this.props.user ? this.submitEdit : this.submitNew}>
                <h1> Registar {this.state.type}</h1>
                <p> Tipo de Registo: </p>
                <div className="radio">
                <label>
                <input 
                    type="radio"
                    name='type'
                    value="Medico"
                    onChange={this.myChangeHandler}
                        />
                        Médico &emsp;
                </label>
                    <b/>
                <label>
                <input
                     type="radio"
                     name='type'
                     value="Paciente"
                     onChange={this.myChangeHandler}
                    />
                    Paciente
                </label>
                </div>
                <p>Insira o nome:</p>
                <input
                    type="text"
                    name='name'
                    onChange={this.myChangeHandler}
                />
                <p>Insira a data de nascimento:</p>
                <input
                    type='date'
                    name='dataNascimento'
                    onChange={this.myChangeHandler}
                />
                <p>Insira o email:</p>
                <input
                    type="email"
                    name='email'
                    onChange={this.myChangeHandler}
                />
                <p>Insira a password:</p>
                <input
                    type="password"
                    name='password'
                    onChange={this.myChangeHandler}
                />
                <p>Insira a morada:</p>
                <input
                    type="text"
                    name='morada'
                    onChange={this.myChangeHandler}
                />
                <p>Insira a localidade:</p>
                <input
                    type="text"
                    name='localidade'
                    onChange={this.myChangeHandler}
                />
                <p>Insira o NIF:</p>
                <input
                    type="text"
                    name='nif'
                    onChange={this.myChangeHandler}
                />
                <p>Insira a Codigo-Postal:</p>
                <input
                    type="text"
                    name='codigo_postal'
                    placeholder="XXXX-XXX"
                    required pattern="\d{4}-\d{3}"
                    onChange={this.myChangeHandler}
                />
                <p>Insira os seus contactos:</p>
                <input
                    type="text"
                    name='contactos'
                    onChange={this.myChangeHandler}
                />
                <br />
                <br />
                <input type='submit' value="Registar"/>
            </form>
        );
    }
}
