import React, { Component } from 'react';
import { BrowserRouter, Route, Switch, Redirect } from "react-router-dom";
import { instanceOf } from 'prop-types';
import { withCookies, Cookies } from 'react-cookie';
import { Layout } from './components/Layout';
import { Layout2 } from './components/Layout2';
import { Home } from './components/Home';
import { Registar } from './components/Registar';
import { Login } from './components/Login';
import { Logout } from './components/Logout';
import { isAuthenticated } from './components/Login';
import { Perfil } from './components/Perfil'
import { Privacy } from './components/Privacy'

import './custom.css'

//export const CONTAS_API_URL = 'https://localhost:5001/api/contas';

const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route {...rest} render={props => (
            isAuthenticated() ? (
                <Component {...props} />
            ) : (
                <Redirect to={{ pathname: '/login', state: { from: props.location } }} />
            )
     )} />
)

class App extends Component {
    static displayName = App.name;

    static propTypes = {
        cookies: instanceOf(Cookies).isRequired
    };

    constructor(props) {
        super(props);

        const { cookies } = props;
        this.state = {
            name: cookies.get('name') || 'Ben'
        };
    }

    handleNameChange(name) {
        const { cookies } = this.props;

        cookies.set('name', name, { path: '/' });
        this.setState({ name });
    }

    render() {
      const { name } = this.state;
      return (
          !isAuthenticated() ? (
            <Layout>
                <Route exact path='/' component={Home} />
                <Route path='/privacy' component={Privacy} />
                <Route path='/login' component={Login} />
                <Route path='/registar' component={Registar} />
                <PrivateRoute path='/perfil' component={Perfil} />
              </Layout>
          ) : (
                  <Layout2>
                      <Route path='/perfil' component={Perfil} />
                      <Route path='/logout' component={Logout} />
                      <Route path='/privacy' component={Privacy} />
                  </Layout2>      
                  )
        );
        <div>
            <NameForm name={name} onChange={this.handleNameChange.bind(this)} />
            {this.state.name && <h1>Hello {this.state.name}!</h1>}
        </div>
    }
}

export default withCookies(App);
