import React, { Component } from 'react';
import EditButton from 'react-edit-button';
import { Collapse, Container, Navbar, NavbarBrand, NavbarToggler, NavItem, NavLink } from 'reactstrap';
import { Link } from 'react-router-dom';

import ImgPerfil from './images/profile-placeholder.jpg'
import { userId } from './Login';

export class PerfilPaciente extends Component {
    static displayName = PerfilPaciente.name;

    constructor(props) {
        super(props);
        this.state = {
            id: '',
            editing: false
        };
    }

    componentDidMount() {
        this.state.id = userId;
    }

    handleOnAccept = () => {
        this.state.editing = true;
    }

    render() {
        return (
            <div>
                <h1> Perfil {this.state.id}</h1>
                <div className="img">
                    <img height={200} src={ImgPerfil} />
                </div>
                <EditButton onAccept={this.handleOnAccept}>
                    <span>Edit</span>
                </EditButton>
            </div>
        )
    }

}
