import React, { Component } from 'react';
import { Collapse, Container, Navbar, NavbarBrand, NavbarToggler, NavItem, NavLink } from 'reactstrap';
import { Link } from 'react-router-dom';


import ImgPerfil from './images/profile-placeholder.jpg'


export class PerfilPaciente extends Component {
    static displayName = PerfilPaciente.name;

    constructor(props) {
        super(props);
        this.state = {
            editing: false
        };
        this.newbirthYear = "";
        this.newHomeWorld = "";
    }

    render() {
        const { imgSrc, cardName, birthYear, homeWorld, onEdit } = this.props;
        return (
            <div className="card">
                <div className="card-content">
                    <div className="card-name">{cardName}</div>
                    <img src={`http://localhost:3008/${imgSrc}`} alt="profile" />
                    <p>
                        <span>Birthday:</span>
                        {this.state.editing ? (
                            <span className="birth-year">{birthYear}</span>
                        ) : (
                                <input
                                    type="text"
                                    defaultValue={birthYear}
                                    ref={node => {
                                        this.newbirthYear = node;
                                    }}
                                />
                            )}
                    </p>
                    <p>
                        <span>Homeworld:</span>
                        {this.state.editing ? (
                            <span className="home-world">{homeWorld}</span>
                        ) : (
                                <input
                                    type="text"
                                    defaultValue={homeWorld}
                                    ref={node => {
                                        this.newHomeWorld = node;
                                    }}
                                />
                            )}
                    </p>
                    <div align="center">
                        <button
                            onClick={() => {
                                this.setState({ editing: true });
                            }}
                        >
                            Edit
            </button>
                    </div>
                </div>
            </div>
        );
    }





