    //            alert("Credenciais Inv�lidas!");
    //            console.log(response);
    //        })
    //}

    handlerLogin = (event) => {

        event.preventDefault();

        axios.get('https://localhost:5001/api/contas')
            .then(response => {
                alert("Login efetuado com sucesso.");
                console.log(response);
                this.state.valido = true;
                login(this.state.valido);
                this.props.history.push("/perfil");
            })
            .catch(error => {
                alert("Credenciais Inv�lidas! " + error);
                console.log(error);
            })

        axios.get('https://localhost:5001/api/contas/login', {
            params: {
                Email: this.state.email,
                Password: this.state.password
            }
        })
            .then(response => {
                alert("Login efetuado com sucesso.");
                console.log(response);
                this.state.valido = true;
                login(this.state.valido);
                this.props.history.push("/perfil");
            })
            .catch(error => {
                alert("Credenciais Inv�lidas! " + error);
                console.log(error);
            })
        //event.preventDefault();
    }

